import { uiActions } from "./ui-slice";
export const sendCartData = (cart) => {
    return async (dispatch) => {
      dispatch(
        uiActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sent cart data successfully!",
        })
      );
  
      const sendRequest = async () => {
        const response = await fetch(
          "https://mealsapp-38926-default-rtdb.europe-west1.firebasedatabase.app/cart.json",
          {
            method: "PUT",
            body: JSON.stringify(cart),
          }
        );
        if (!response.ok) {
          throw new Error("Sending cart data failed");
        }
      };
      try {
        await sendRequest();
  
        dispatch(
          uiActions.showNotification({
            status: "success",
            title: "Success!",
            message: "Sent cart data successfully!",
          })
        );
      } catch (error) {
        dispatch(
          uiActions.showNotification({
            status: "erorr",
            title: "Erorr!",
            message: "Sent cart data unsuccessfully!",
          })
        );
      }
    };
  };
