import { useSelector } from "react-redux";
import { Fragment, useEffect } from "react";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useDispatch } from "react-redux";
import { uiActions } from "./components/store/ui-slice";
import Notification from './components/UI/Notification'

function App() {
  const dispatch = useDispatch();
  const notification = useSelector(state => state.ui.notification)
  const cart = useSelector((state) => state.cart);
  const showCart = useSelector((state) => state.ui.cartIsVisible);
  useEffect(() => {
    const sendCartData = async () => {
      const response = await fetch(
        "https://mealsapp-38926-default-rtdb.europe-west1.firebasedatabase.app/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!response.ok) {
        throw new Error("Sending cart data failed");
      }
      const responseData = await response.json();
    };

    dispatch(
      uiActions.showNotification({
        status: "success",
        title: "SUccess!",
        message: "Sent cart data successfully!",
      })
    );

    sendCartData().catch((error) => {
      dispatch(
        uiActions.showNotification({
          status: "erorr",
          title: "Erorr!",
          message: "Sent cart data unsuccessfully!",
        })
      );
    });
  }, [cart, dispatch]);

  return (
    <Fragment>

    <Layout>
      {notification && <Notification status={notification.status} message={notification.message} title={notification.title}/>}
      {showCart && <Cart />}
      <Products />
    </Layout>
    </Fragment>
  );
}

export default App;
