import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
  },
  reducers: {
    removeFromCart(state, action) {
        const newItem = action.payload;
        const exsistingItem = state.items.find(item => item.id === newItem.id)
        if(!exsistingItem){
            state.items.push({
                itemId: newItem.id, price: newItem.price, quantity: 1, totalPrice: newItem.price, name:newItem.title
            })
        }else {
            exsistingItem.quantity++;
            exsistingItem.totalPrice = exsistingItem.totalPrice + newItem.price;
        }
    },
    addToCart(state,action) {},
  },
});
